import React, { useState } from 'react'
import NextImage from 'next/image'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

export function ImageWithFallback(props: any) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, sizes, priority, ...rest } = props

  if (didError) {
    return (
      <div
        className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
        style={style}
      >
        <div className="flex items-center justify-center w-full h-full">
          <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
        </div>
      </div>
    )
  }

  // Render Next.js Image (uses fill, so parent must be position:relative and have size)
  return (
    <div className={`relative ${className ?? ''}`} style={style}>
      <NextImage src={src || ''} alt={alt || ''} fill className="object-cover" onError={handleError} sizes={sizes ?? '100vw'} priority={priority} {...rest} />
    </div>
  )
}
